import React from "react";
import styled from "styled-components";
import { TiTick } from "react-icons/ti";
function Membership() {
  return (
    <MembershipContainer>
      <MadrasSection>
        <AboutContainer>
          <LearnText>Learn to know</LearnText>
          <MSText>IEEE madras section</MSText>
          <MsPara>
            IEEE is the world's largest technical professional organization
            dedicated to advancing technology for the benefit of humanity.
          </MsPara>
          <MsButton>
            <a href="https://www.ieeemadras.org/" target="_blank">Visit website</a>
          </MsButton>
        </AboutContainer>
      </MadrasSection>
      <MembershipSection>
        <MemberShipText>Membership</MemberShipText>
        <MemberMotto>
          Come be a part of the legacy and make technology better!
        </MemberMotto>
        <MembershipCards>
          <MemberShipCard>
            <CardTitle>Benefits</CardTitle>
            <CardContents>
              <CardCondition>
                <IconContainer>
                  <TiTick />
                </IconContainer>
                <MemberCardContent>
                  New resources, opportunities and many discounts{" "}
                </MemberCardContent>
              </CardCondition>
              <CardCondition>
                <IconContainer>
                  <TiTick />
                </IconContainer>
                <MemberCardContent>
                  Advance your career in the right direction{" "}
                </MemberCardContent>
              </CardCondition>
              <CardCondition>
                <IconContainer>
                  <TiTick />
                </IconContainer>
                <MemberCardContent>
                  Chance to participate in various IEEE conferences across the
                  globe{" "}
                </MemberCardContent>
              </CardCondition>
            </CardContents>
            <CardButton>Know More</CardButton>
          </MemberShipCard>
          <MemberShipCard>
            <CardTitle>Join a society</CardTitle>
            <CardContents>
              <CardCondition>
                <IconContainer>
                  <TiTick />
                </IconContainer>
                <MemberCardContent>
                  Stay updated with latest trends in technology{" "}
                </MemberCardContent>
              </CardCondition>
              <CardCondition>
                <IconContainer>
                  <TiTick />
                </IconContainer>
                <MemberCardContent>
                  Network with colleagues locally and abroad{" "}
                </MemberCardContent>
              </CardCondition>
              <CardCondition>
                <IconContainer>
                  <TiTick />
                </IconContainer>
                <MemberCardContent>
                  Collaborate on research and projects with leading experts and
                  many such opportunities{" "}
                </MemberCardContent>
              </CardCondition>
            </CardContents>
            <CardButton>View More</CardButton>
          </MemberShipCard>
          <MemberShipCard>
            <CardTitle>Join IEEE</CardTitle>
            <CardContents>
              <CardCondition>
                <IconContainer>
                  <TiTick />
                </IconContainer>
                <MemberCardContent>
                  Be a part of the world's largest technology community{" "}
                </MemberCardContent>
              </CardCondition>
              <CardCondition>
                <IconContainer>
                  <TiTick />
                </IconContainer>
                <MemberCardContent>
                  Acquire new skills, and advance your professional growth and
                  development{" "}
                </MemberCardContent>
              </CardCondition>
              <CardCondition>
                <IconContainer>
                  <TiTick />
                </IconContainer>
                <MemberCardContent>
                  New resources, opportunities and many discounts{" "}
                </MemberCardContent>
              </CardCondition>
            </CardContents>
            <CardButton>View More</CardButton>
          </MemberShipCard>
        </MembershipCards>
      </MembershipSection>
    </MembershipContainer>
  );
}

export default Membership;

const MembershipContainer = styled.div`
  margin-top: 80px;
`;

const MadrasSection = styled.div``;

const LearnText = styled.p`
  font-size: 1.5rem;
  font-weight: 700;
  text-align: center;
  font-family: "Montserrat", sans-serif;
  color: #005f94;
  letter-spacing: 2px;
  text-transform: uppercase;
`;
const MSText = styled.p`
  font-size: 24px;
  font-weight: bold;
  text-align: center;
  font-family: "Montserrat", sans-serif;
  letter-spacing: 1px;
  margin-top: 20px;
`;

const MsPara = styled.p`
  text-align: center;
  font-size: 1rem;
  font-family: "Montserrat", sans-serif;
  letter-spacing: 1px;
  margin-top: 20px;
  padding: 20px;
  font-weight: 500;
`;

const AboutContainer = styled.div`
  position: relative;

`;

const MsButton = styled.button`
  width: 160px;
  height: 50px;
  color: #fff;
  background: #005f94;
  font-size: 1.2rem;
  border: none;
  text-transform: uppercase;
  cursor: pointer;
  font-weight: 500;
  position: absolute;
  left:50%;
  top:80%;
  transform:translate(-50%,50%);
  a {
    text-decoration: none;
    color: #fff;
  }

  @media screen and (max-width: 768px) {
    width: 120px;
    height: 40px;
    font-size: 0.8rem;
  }
`;

const MembershipSection = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  margin-top: 30px;
`;

const MemberShipText = styled.h2`
  font-size: 1.5rem;
  font-weight: 700;
  text-align: center;
  font-family: "Montserrat", sans-serif;
  letter-spacing: 2px;
`;

const MemberMotto = styled.p`
  font-size: 1rem;
  font-weight: 400;
  text-align: center;
  font-family: "Montserrat", sans-serif;
  color: #005f94;
  letter-spacing: 2px;
  margin-top: 20px;
`;

const MembershipCards = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  margin-top: 20px;
  align-items: center;
  justify-content: space-around;
  padding: 20px;
  @media screen and (max-width: 958px) {
    flex-direction: column;
  }
`;

const MemberShipCard = styled.div`
  margin-top: 20px;
  width: 100%;
  border: 1px solid #dee2e6;
  height: 500px;
  margin-right: 10px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const CardTitle = styled.h2`
  margin-top: 20px;
  font-size: 1.5rem;
  font-weight: 600;
  text-align: center;
  font-family: "Montserrat", sans-serif;
`;

const CardCondition = styled.div`
  display: flex;
  padding: 10px;
  margin-top: 20px;
`;

const IconContainer = styled.div`
  color: green;
  font-size: 15px;
`;

const MemberCardContent = styled.div`
  font-size: 1rem;
  font-weight: 500;
  font-family: "Montserrat", sans-serif;
  margin-left: 10px;
